    private void calButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calButtonActionPerformed

        totalArea.setText(null);
        
        double precio;
        double cantidad;
        double imco;
        double imds;
        double pagar;
        
        precio = Double.parseDouble(this.precText.getText());
        cantidad = Double.parseDouble(this.cantext.getText());
        
        imco = (precio * cantidad);
        imds = (imco * 0.15);
        pagar = (imco - imds);

        this.totalArea.append("El importe de compra es: S/."+imco);
        this.totalArea.append("\nEl descuento es: S/."+imds);
        this.totalArea.append("\nEl pago total es: S/."+pagar);
    }//GEN-LAST:event_calButtonActionPerformed

