    private void calButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calButtonActionPerformed

        totalArea.setText(null);

        double precio;
        int cantidad;
        double subida;
        double pss;
        double importt;
        int chocolate;

        precio = Double.parseDouble(this.precText.getText());
        cantidad = Integer.parseInt(this.cantext.getText());
        
        //ALGORITMO
        subida = (precio * 0.35);
        pss = (precio + subida);
        importt = (pss * cantidad);
        chocolate = (cantidad * 3);

        this.totalArea.append("El aumento de precio es: S/."+subida);
        this.totalArea.append("\nPrecio por Semana Santa: S/."+pss);
        this.totalArea.append("\nImporte Total: S/."+importt);
        this.totalArea.append("\nChocolates entregados: S/."+chocolate);
    }//GEN-LAST:event_calButtonActionPerformed

