    private void calButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_calButtonActionPerformed

        totalArea.setText(null);

        double precio;
        int cantidad;
        double transporte;
        double imcom;
        double imds;
        double impa;
        
        precio = Double.parseDouble(this.precText.getText());
        cantidad = Integer.parseInt(this.cantext.getText());
        transporte = Double.parseDouble(this.transText.getText());

        //ALGORITMO
        imcom = (precio * cantidad);
        imds = (imcom * 0.12);
        impa = (imcom - imds)+ transporte;

        this.totalArea.append("El importe de compra es: S/."+imcom);
        this.totalArea.append("\nEl importe de descuento es: S/."+imds);
        this.totalArea.append("\nEl importe pagar total es: S/."+impa);

    }//GEN-LAST:event_calButtonActionPerformed

